mapper.select:
        SELECT


--         级联表的字段
        ,jiaoshi.jiaoshi_name as jiaoshiName
        ,jiaoshi.jiaoshi_id_number as jiaoshiIdNumber
        ,jiaoshi.jiaoshi_phone as jiaoshiPhone
        ,jiaoshi.jiaoshi_photo as jiaoshiPhoto
        ,jiaoshi.jiaoshi_delete as jiaoshiDelete
        ,xuesheng.xuesheng_name as xueshengName
        ,xuesheng.xuesheng_id_number as xueshengIdNumber
        ,xuesheng.xuesheng_phone as xueshengPhone
        ,xuesheng.xuesheng_photo as xueshengPhoto
        ,xuesheng.xuesheng_yixiu as xueshengYixiu
        ,xuesheng.xuesheng_yingxiu as xueshengYingxiu
        ,xuesheng.xuesheng_delete as xueshengDelete
        ,users.username as uusername
        ,users.password as upassword
        ,users.role as urole
        ,users.addtime as uaddtime

        FROM forum  a
        left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id
        left JOIN xuesheng xuesheng ON a.xuesheng_id = xuesheng.id
        left JOIN users users ON a.users_id = users.id



                and a.id in

                #{item}



                and a.forum_name like CONCAT('%',#{params.forumName},'%')


                and a.xuesheng_id = #{params.xueshengId}


                and a.jiaoshi_id = #{params.jiaoshiId}


                and a.users_id = #{params.usersId}


                and a.forum_content like CONCAT('%',#{params.forumContent},'%')


                  and a.super_ids >= #{params.superIdsStart}   


                  and a.super_ids <= #{params.superIdsEnd}   


                and a.super_ids = #{params.superIds}


                and a.forum_state_types = #{params.forumStateTypes}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




                and a.jiaoshi_id IS NOT NULL


                and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')


                and jiaoshi.jiaoshi_id_number like CONCAT('%',#{params.jiaoshiIdNumber},'%')


                and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')


                  and jiaoshi.jiaoshi_delete >= #{params.jiaoshiDeleteStart}   


                  and jiaoshi.jiaoshi_delete <= #{params.jiaoshiDeleteEnd}   


                and jiaoshi.jiaoshi_delete = #{params.jiaoshiDelete}



                and a.xuesheng_id IS NOT NULL


                and xuesheng.xuesheng_name like CONCAT('%',#{params.xueshengName},'%')


                and xuesheng.xuesheng_id_number like CONCAT('%',#{params.xueshengIdNumber},'%')


                and xuesheng.xuesheng_phone like CONCAT('%',#{params.xueshengPhone},'%')


                  and xuesheng.xuesheng_yixiu >= #{params.xueshengYixiuStart}   


                  and xuesheng.xuesheng_yixiu <= #{params.xueshengYixiuEnd}   


                and xuesheng.xuesheng_yixiu = #{params.xueshengYixiu}


                  and xuesheng.xuesheng_yingxiu >= #{params.xueshengYingxiuStart}   


                  and xuesheng.xuesheng_yingxiu <= #{params.xueshengYingxiuEnd}   


                and xuesheng.xuesheng_yingxiu = #{params.xueshengYingxiu}


                  and xuesheng.xuesheng_delete >= #{params.xueshengDeleteStart}   


                  and xuesheng.xuesheng_delete <= #{params.xueshengDeleteEnd}   


                and xuesheng.xuesheng_delete = #{params.xueshengDelete}



                and a.users_id IS NOT NULL


                and users.username like CONCAT('%',#{params.username},'%')


                and users.password like CONCAT('%',#{params.password},'%')


                and users.role like CONCAT('%',#{params.role},'%')


                  and UNIX_TIMESTAMP(users.addtime) >= UNIX_TIMESTAMP(#{params.addtimeStart}) 


                  and UNIX_TIMESTAMP(users.addtime) <= UNIX_TIMESTAMP(#{params.addtimeEnd}) 



        order by a.${params.orderBy} desc
